import {React, useState, useEffect}  from 'react'
import './style/message.css'
import useAxios from '../utils/useAxios'
import jwtDecode from 'jwt-decode'
import moment from 'moment'
import {link} from 'react-router-dom/'
import { Link } from 'react-router-dom/cjs/react-router-dom.min'


function Messsage() {

    // Define base api url
    const baseURL = 'http://127.0.0.1:8000/'

    const [messages, setMesssage] = useState([])

    const axios = useAxios()
    const token = localStorage.getItem('authTokens');
    const tokenObject = JSON.parse(token);
    const user_id = jwtDecode(tokenObject.access).user_id


    useEffect(() => {
        try {
            axios.get(baseURL + 'my-messages/' + user_id + '/').then((res) => {
                setMesssage(res.data);
            })         
        }
        catch (error) {
            console.log(error);
        }

    })

  return (
    <div>
        <main className="content" style={{marginTop:"150px"}}>
            <div className="container p-0">
                <h1 className="h3 mb-3">Messages</h1>
                <div className="card">
                <div className="row g-0">
                    <div className="col-12 col-lg-5 col-xl-3 border-right">
                    <div className="px-4 d-none d-md-block">
                        <div className="d-flex align-items-center">
                        <div className="flex-grow-1">
                            <input
                            type="text"
                            className="form-control my-3"
                            placeholder="Search..."
                            />
                        </div>
                        </div>
                    </div>
                    {messages.map((message) =>

                        <Link to={'/inbox/' + message.sender}            
                        className="list-group-item list-group-item-action border-0"
                    >                        
                        <div className="badge bg-success float-right">{moment.utc(message.created_at).local().startOf('seconds').fromNow().toString()}</div>
                        <div className="d-flex align-items-start">
                        <img
                            src="https://bootdey.com/img/Content/avatar/avatar5.png"
                            className="rounded-circle mr-1"
                            alt="Vanessa Tucker"
                            width={40}
                            height={40}
                        />
                        <div className="flex-grow-1 ml-3">
                            {message.sender_first_name}
                            <div className="small">
                             {message.message}
                            </div>

                        </div>
                        </div>
                        </Link>

                    )}

                   </div>
                </div>
                </div>
            </div>
        </main>
    </div>
  )
}

export default Messsage
